import React from 'react';
import { Form, Input, Select, Button } from 'antd';
import { UserOutlined } from '@ant-design/icons';

const { Option } = Select;

const ReservationForm = ({ onSubmit, loading }) => {
  const [form] = Form.useForm();

  const handleSubmit = (values) => {
    onSubmit({
      numSeats: values.numSeats,
      personName: values.personName,
      needsAccessible: values.needsAccessible === 'yes'
    });
    form.resetFields();
  };

  return (
    <Form
      form={form}
      layout="vertical"
      onFinish={handleSubmit}
      size="large"
    >
      <Form.Item
        label="Number of Seats"
        name="numSeats"
        rules={[
          { required: true, message: 'Please enter number of seats' },
          { type: 'number', min: 1, max: 10, message: 'Number must be between 1 and 10' }
        ]}
      >
        <Input
          type="number"
          placeholder="Enter number of seats"
          min={1}
          max={10}
        />
      </Form.Item>

      <Form.Item
        label="Your Name"
        name="personName"
        rules={[
          { required: true, message: 'Please enter your name' },
          { min: 2, message: 'Name must be at least 2 characters' }
        ]}
      >
        <Input
          prefix={<UserOutlined />}
          placeholder="Enter your name"
        />
      </Form.Item>

      <Form.Item
        label="Wheelchair Accessible Seats"
        name="needsAccessible"
        rules={[{ required: true, message: 'Please select accessibility requirement' }]}
      >
        <Select placeholder="Do you need wheelchair accessible seats?">
          <Option value="no">No</Option>
          <Option value="yes">Yes</Option>
        </Select>
      </Form.Item>

      <Form.Item>
        <Button
          type="primary"
          htmlType="submit"
          loading={loading}
          block
          size="large"
        >
          Reserve Seats
        </Button>
      </Form.Item>
    </Form>
  );
};

export default ReservationForm;
